/* actions.js */


export const oldAddNote = function (store) {
    var dispatch = store.dispatch
    dispatch('ADD_NOTE')
}

export const addNote = ({dispatch}) => {
    dispatch('ADD_NOTE')
}

export const editNote = ({dispatch}, n) => {
    dispatch('EDIT_NOTE', n.target.value)
}

export const deleteNode = ({dispatch}) => {
    dispatch('DELETE_NOTE')
}

export const updateActiveNote = ({dispatch}, note) => {
    dispatch('SET_ACTIVE_NOTE', note)
}

export const addFavoriteNote = ({dispatch}) => {
    dispatch('ADD_FAVORITE_NOTE')
}